// src/pages/Dashboard.jsx
import { useEffect, useState } from "react";
import "./Dashboard.css";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  LineElement,
  CategoryScale,
  LinearScale,
  PointElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend);

export default function Dashboard() {
  const [sensorData, setSensorData] = useState({
    tds: 0,
    ph: 0,
    tempAir: 0,
    tempUdara: 0,
    humidity: 0,
    timestamp: new Date().toISOString(),
  });

  const [selectedParam, setSelectedParam] = useState("ph");
  const [history, setHistory] = useState({ ph: [], tds: [], tempAir: [], tempUdara: [], humidity: [], labels: [] });

  useEffect(() => {
    const interval = setInterval(() => {
      fetch("http://192.168.37.23:5000/api/data")
        .then((res) => res.json())
        .then((data) => {
          console.log('ðŸ“¦ Data dari API:', data);  // â† Cek apakah semua field muncul
          setSensorData(data);
          setHistory((prev) => ({
            ...prev,
            ph: [...prev.ph.slice(-9), data.ph],
            tds: [...prev.tds.slice(-9), data.tds],
            tempAir: [...prev.tempAir.slice(-9), data.tempAir],
            tempUdara: [...prev.tempUdara.slice(-9), data.tempUdara],
            humidity: [...prev.humidity.slice(-9), data.humidity],
            labels: [...prev.labels.slice(-9), new Date(data.timestamp).toLocaleTimeString()],
          }));
        });
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const handleCardClick = (param) => {
    setSelectedParam(param);
  };

  const controlPump = async (id, status) => {
    try {
      await fetch(`http://192.168.37.23:5000/api/pump/${id}/${status}`, {
        method: 'POST',
      });
      alert(`Pompa ${id} ${status}`);
    } catch (err) {
      alert('Gagal kontrol pompa!');
    }
  };



  const chartData = {
    labels: history.labels,
    datasets: [
      {
        label: selectedParam,
        data: history[selectedParam],
        fill: false,
        borderColor: "#4ade80",
        backgroundColor: "#4ade80",
        tension: 0.4,
      },
    ],
  };

  return (
    <div className="dashboard">
      <h1 className="dashboard-title">SwÃ¡sÃªma Dashboard</h1>
      <p className="timestamp">{new Date(sensorData.timestamp).toLocaleString()}</p>

      <div className="sensor-grid">
        <div
          className={`sensor-card ${selectedParam === "ph" ? "active" : ""}`}
          onClick={() => handleCardClick("ph")}
        >
          <h2>pH Air</h2>
          <p>{sensorData.ph}</p>
        </div>
        <div
          className={`sensor-card ${selectedParam === "tds" ? "active" : ""}`}
          onClick={() => handleCardClick("tds")}
        >
          <h2>TDS</h2>
          <p>{sensorData.tds} ppm</p>
        </div>
        <div
          className={`sensor-card ${selectedParam === "tempAir" ? "active" : ""}`}
          onClick={() => handleCardClick("tempAir")}
        >
          <h2>Suhu Air</h2>
          <p>{sensorData.tempAir} Â°C</p>
        </div>
        <div
          className={`sensor-card ${selectedParam === "tempUdara" ? "active" : ""}`}
          onClick={() => handleCardClick("tempUdara")}
        >
          <h2>Temp Udara</h2>
          <p>{sensorData.tempUdara} Â°C</p>
        </div>
        <div
          className={`sensor-card ${selectedParam === "humidity" ? "active" : ""}`}
          onClick={() => handleCardClick("humidity")}
        >
          <h2>Kelembapan</h2>
          <p>{sensorData.humidity} %</p>
        </div>
      </div>

      <div className="panel">
        <Line data={chartData} />
      </div>

      <div className="panel control-panel">
        <h2>Kontrol Pompa</h2>
        <div className="button-grid">
          <button onClick={() => controlPump('A', 'on')}>Pompa A ON</button>
          <button onClick={() => controlPump('A', 'off')}>Pompa A OFF</button>
          <button onClick={() => controlPump('B', 'on')}>Pompa B ON</button>
          <button onClick={() => controlPump('B', 'off')}>Pompa B OFF</button>
        </div>
      </div>
    </div>
  );
}
