// src/pages/Dashboard.jsx
import { useEffect, useState } from "react";
import "../App.css"

export default function Dashboard() {
  const [sensorData, setSensorData] = useState({
    tds: 0,
    ph: 0,
    tempAir: 0,
    tempUdara: 0,
    humidity: 0,
    timestamp: new Date().toISOString(),
  });

  useEffect(() => {
    const interval = setInterval(() => {
      fetch("http://localhost:5000/api/data")
        .then((res) => res.json())
        .then((data) => setSensorData(data));
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="dashboard">
      <h1 className="dashboard-title">Swásêma Dashboard</h1>
      <p className="timestamp">{new Date(sensorData.timestamp).toLocaleString()}</p>

      <div className="sensor-grid">
        <div className="sensor-card">
          <h2>pH Air</h2>
          <p>{sensorData.ph}</p>
        </div>
        <div className="sensor-card">
          <h2>TDS</h2>
          <p>{sensorData.tds} ppm</p>
        </div>
        <div className="sensor-card">
          <h2>Suhu Air</h2>
          <p>{sensorData.tempAir} °C</p>
        </div>
        <div className="sensor-card">
          <h2>Temp Udara</h2>
          <p>{sensorData.tempUdara} °C</p>
        </div>
        <div className="sensor-card">
          <h2>Kelembapan</h2>
          <p>{sensorData.humidity} %</p>
        </div>
      </div>

      <div className="panel">
        <p>Grafik akan ditampilkan di sini (fitur dalam pengembangan).</p>
      </div>

      <div className="panel">
        <p>Panel kontrol pompa akan hadir di sini (fitur dalam pengembangan).</p>
      </div>
    </div>
  );
}
