// src/pages/Dashboard.jsx
import { useEffect, useState } from "react";
import "./Dashboard.css";
import {
  RadialBarChart,
  RadialBar,
  ResponsiveContainer,
  PolarAngleAxis,
} from "recharts";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  LineElement,
  CategoryScale,
  LinearScale,
  PointElement,
  Tooltip,
  Legend,
} from "chart.js";

ChartJS.register(LineElement, CategoryScale, LinearScale, PointElement, Tooltip, Legend);

export default function Dashboard() {
  const [sensorData, setSensorData] = useState({
    tds: 0,
    ph: 0,
    tempAir: 0,
    tempUdara: 0,
    humidity: 0,
    timestamp: new Date().toISOString(),
  });

  const [selectedParam, setSelectedParam] = useState("ph");
  const [history, setHistory] = useState({ ph: [], tds: [], tempAir: [], tempUdara: [], humidity: [], labels: [] });

  useEffect(() => {
    const interval = setInterval(() => {
      fetch("http://192.168.37.23:5000/api/data")
        .then((res) => res.json())
        .then((data) => {
          console.log('📦 Data dari API:', data);  // ← Cek apakah semua field muncul
          setSensorData(data);
          setHistory((prev) => ({
            ...prev,
            ph: [...prev.ph.slice(-9), data.ph],
            tds: [...prev.tds.slice(-9), data.tds],
            tempAir: [...prev.tempAir.slice(-9), data.tempAir],
            tempUdara: [...prev.tempUdara.slice(-9), data.tempUdara],
            humidity: [...prev.humidity.slice(-9), data.humidity],
            labels: [...prev.labels.slice(-9), new Date(data.timestamp).toLocaleTimeString()],
          }));
        });
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  const handleCardClick = (param) => {
    setSelectedParam(param);
  };

  const controlPump = async (id, status) => {
    try {
      await fetch(`http://192.168.37.23:5000/api/pump/${id}/${status}`, {
        method: 'POST',
      });
      alert(`Pompa ${id} ${status}`);
    } catch (err) {
      alert('Gagal kontrol pompa!');
    }
  };

const getGaugePercent = (param, value) => {
    let min = 1,
      max = 6;
    switch (param) {
      case "ph":
        min = 4;
        max = 8;
        break;
      case "tds":
        min = 600;
        max = 1400;
        break;
      case "tempAir":
        min = 17;
        max = 28;
        break;
      case "tempUdara":
        min = 17;
        max = 27;
        break;
      case "humidity":
        min = 45;
        max = 93;
        break;
    }
    return Math.min(Math.max((value - min) / (max - min), 0), 1);
  };

  const getStatus = (param, value) => {
    if (param === "ph") {
      if (value < 5.0 || value > 7.0) return "Poor";
      if (value < 5.8 || value > 6.4) return "Fair";
      return "Excellent";
    } else if (param === "tds") {
      if (value < 700 || value > 1300) return "Poor";
      if (value < 800 || value > 1200) return "Fair";
      return "Excellent";
    } else if (param === "tempAir") {
      if (value < 18 || value > 27) return "Poor";
      if (value < 20 || value > 25) return "Fair";
      return "Excellent";
    } else if (param === "tempUdara") {
      if (value < 18 || value > 26) return "Poor";
      if (value < 20 || value > 24) return "Fair";
      return "Excellent";
    } else if (param === "humidity") {
      if (value < 55 || value > 83) return "Poor";
      if (value < 65 || value > 78) return "Fair";
      return "Excellent";
    }
    return "Excellent";
  };

  const renderCard = (param, label, value) => {
    const status = getStatus(param, value);
    return (
      <div
        className={`sensor-card ${selectedParam === param ? "active" : ""}`}
        onClick={() => handleCardClick(param)}
      >
        <h2>{label}</h2>
        <GaugeChart
          id={`gauge-${param}`}
          nrOfLevels={5}
          arcsLength={[0.15, 0.15, 0.4, 0.15, 0.15]}
          colors={["#ef4444", "#facc15", "#4ade80", "#facc15", "#ef4444"]}
          percent={getGaugePercent(param, value)}
          arcPadding={0.02}
          needleColor="#ffffff"
          needleBaseColor="#ffffff"
          hideText
        />
        <p>
          {value}{" "}
          {param === "humidity"
            ? "%"
            : param === "tds"
            ? "ppm"
            : param.includes("temp")
            ? "°C"
            : ""}
        </p>
        <div
          className={`status-text ${
            status === "Fair"
              ? "status-fair"
              : status === "Poor"
              ? "status-poor"
              : ""
          }`}
        >
          {status}
        </div>
      </div>
    );
  };

  const chartData = {
    labels: history.labels,
    datasets: [
      {
        label: selectedParam,
        data: history[selectedParam],
        fill: false,
        borderColor: "#4ade80",
        backgroundColor: "#4ade80",
        tension: 0.4,
      },
    ],
  };

  return (
    <div className="dashboard">
      <h1 className="dashboard-title">Swásêma Dashboard</h1>
      <p className="timestamp">{new Date(sensorData.timestamp).toLocaleString()}</p>

      <div className="sensor-grid">
        <div
          className={`sensor-card ${selectedParam === "ph" ? "active" : ""}`}
          onClick={() => handleCardClick("ph")}
        >
          <h2>pH Air</h2>
          <p>{sensorData.ph}</p>
        </div>
        <div
          className={`sensor-card ${selectedParam === "tds" ? "active" : ""}`}
          onClick={() => handleCardClick("tds")}
        >
          <h2>TDS</h2>
          <p>{sensorData.tds} ppm</p>
        </div>
        <div
          className={`sensor-card ${selectedParam === "tempAir" ? "active" : ""}`}
          onClick={() => handleCardClick("tempAir")}
        >
          <h2>Suhu Air</h2>
          <p>{sensorData.tempAir} °C</p>
        </div>
        <div
          className={`sensor-card ${selectedParam === "tempUdara" ? "active" : ""}`}
          onClick={() => handleCardClick("tempUdara")}
        >
          <h2>Temp Udara</h2>
          <p>{sensorData.tempUdara} °C</p>
        </div>
        <div
          className={`sensor-card ${selectedParam === "humidity" ? "active" : ""}`}
          onClick={() => handleCardClick("humidity")}
        >
          <h2>Kelembapan</h2>
          <p>{sensorData.humidity} %</p>
        </div>
      </div>

      <div className="panel">
        <Line data={chartData} />
      </div>

      <div className="panel control-panel">
        <h2>Kontrol Pompa</h2>
        <div className="button-grid">
          <button onClick={() => controlPump('A', 'on')}>Pompa A ON</button>
          <button onClick={() => controlPump('A', 'off')}>Pompa A OFF</button>
          <button onClick={() => controlPump('B', 'on')}>Pompa B ON</button>
          <button onClick={() => controlPump('B', 'off')}>Pompa B OFF</button>
        </div>
      </div>
    </div>
  );
}
